                  </CardContent>
                </Card>
              );
            })}
          </div>
        </div>

        {/* Completed Lotteries Section */}
        {completedLotteries.length > 0 && (
          <div>
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-2xl font-bold text-gray-900">Recent Results</h2>
