import React, { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Checkbox } from '@/components/ui/checkbox';
import { AlertTriangle, Shield, Calendar, User } from 'lucide-react';

interface AgeVerificationModalProps {
  isOpen: boolean;
  onVerified: () => void;
}

export function AgeVerificationModal({ isOpen, onVerified }: AgeVerificationModalProps) {
  const [birthDate, setBirthDate] = useState('');
  const [agreedToTerms, setAgreedToTerms] = useState(false);
  const [acknowledgedRisks, setAcknowledgedRisks] = useState(false);
  const [error, setError] = useState('');

  const calculateAge = (birthDate: string) => {
    const today = new Date();
    const birth = new Date(birthDate);
    let age = today.getFullYear() - birth.getFullYear();
    const monthDiff = today.getMonth() - birth.getMonth();

    if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birth.getDate())) {
      age--;
    }

    return age;
  };

  const handleVerification = () => {
    setError('');

    if (!birthDate) {
      setError('Please enter your birth date');
      return;
    }

    const age = calculateAge(birthDate);

    if (age < 18) {
      setError('You must be 18 or older to use this platform');
      return;
    }

    if (!agreedToTerms) {
      setError('Please agree to the terms and conditions');
      return;
    }

    if (!acknowledgedRisks) {
      setError('Please acknowledge the gaming risks');
      return;
    }

    localStorage.setItem('ageVerified', 'true');
    localStorage.setItem('verificationDate', new Date().toISOString());
    onVerified();
  };

  return (
    <Dialog open={isOpen} onOpenChange={() => {}}>
      <DialogContent className="max-w-md mx-auto bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 border-2 border-gold-400 text-white">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2 text-xl font-bold text-gold-400">
            <Shield className="w-6 h-6" />
            Age Verification Required
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          <div className="bg-red-900/30 border border-red-500 rounded-lg p-4">
            <div className="flex items-start gap-3">
              <AlertTriangle className="w-6 h-6 text-red-400 mt-0.5 flex-shrink-0" />
              <div>
                <h3 className="font-semibold text-red-400 mb-2">Important Gaming Notice</h3>
                <ul className="text-sm space-y-1 text-red-200">
                  <li>‚Ä¢ This platform involves real money transactions</li>
                  <li>‚Ä¢ Gaming can be addictive and cause financial loss</li>
                  <li>‚Ä¢ Only spend what you can afford to lose</li>
                  <li>‚Ä¢ Seek help if gambling becomes problematic</li>
                </ul>
              </div>
            </div>
          </div>

          <div className="space-y-4">
            <div>
              <Label htmlFor="birthDate" className="flex items-center gap-2 text-gold-300">
                <Calendar className="w-4 h-4" />
                Date of Birth
              </Label>
              <Input
                id="birthDate"
                type="date"
                value={birthDate}
                onChange={(e) => setBirthDate(e.target.value)}
                className="bg-gray-800/50 border-gray-600 text-white"
                max={new Date().toISOString().split('T')[0]}
              />
            </div>

            <div className="space-y-3">
              <div className="flex items-start space-x-2">
                <Checkbox
                  id="terms"
                  checked={agreedToTerms}
                  onCheckedChange={(checked) => setAgreedToTerms(checked as boolean)}
                  className="border-gold-400 data-[state=checked]:bg-gold-500"
                />
                <Label htmlFor="terms" className="text-sm leading-relaxed">
                  I agree to the Terms of Service and Privacy Policy, and confirm I am legally permitted to participate in gaming activities in my jurisdiction.
                </Label>
              </div>

              <div className="flex items-start space-x-2">
                <Checkbox
                  id="risks"
                  checked={acknowledgedRisks}
                  onCheckedChange={(checked) => setAcknowledgedRisks(checked as boolean)}
                  className="border-gold-400 data-[state=checked]:bg-gold-500"
                />
                <Label htmlFor="risks" className="text-sm leading-relaxed">
                  I acknowledge the risks of gaming and confirm I will only spend money I can afford to lose.
                </Label>
              </div>
            </div>

            {error && (
              <div className="bg-red-900/50 border border-red-500 rounded p-3 text-red-200 text-sm">
                {error}
              </div>
            )}

            <Button
              onClick={handleVerification}
              className="w-full bg-gradient-to-r from-gold-500 to-gold-600 hover:from-gold-600 hover:to-gold-700 text-black font-semibold"
              data-testid="button-verify-age"
            >
              <User className="w-4 h-4 mr-2" />
              Verify Age & Enter Platform
            </Button>
          </div>

          <div className="text-xs text-gray-400 text-center space-y-1">
            <p>Problem Gambling Resources:</p>
            <p>üá∫üá∏ National Problem Gambling Helpline: 1-800-522-4700</p>
            <p>üåç BeGambleAware.org ‚Ä¢ GamCare.org.uk</p>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/animated-button.ts.content:
import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Button, ButtonProps } from '@/components/ui/button';
import { useSound } from './sound-manager';
import { Sparkles, Zap } from 'lucide-react';

interface AnimatedButtonProps extends Omit<ButtonProps, 'variant'> {
  variant?: 'primary' | 'secondary' | 'success' | 'danger' | 'lottery';
  glowEffect?: boolean;
  sparkleEffect?: boolean;
  soundType?: 'click' | 'success' | 'lottery' | 'coin';
}

export function AnimatedButton({
  children,
  variant = 'primary',
  glowEffect = false,
  sparkleEffect = false,
  soundType = 'click',
  onClick,
  className = '',
  disabled,
  ...props
}: AnimatedButtonProps) {
  const { playSound } = useSound();
  const [isPressed, setIsPressed] = useState(false);
  const [showSparkles, setShowSparkles] = useState(false);

  const handleClick = (e: React.MouseEvent<HTMLButtonElement>) => {
    if (disabled) return;

    playSound(soundType);
    setIsPressed(true);

    if (sparkleEffect) {
      setShowSparkles(true);
      setTimeout(() => setShowSparkles(false), 1000);
    }

    setTimeout(() => setIsPressed(false), 150);

    if (onClick) {
      onClick(e);
    }
  };

  const getVariantClasses = () => {
    switch (variant) {
      case 'primary':
        return 'bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white';
      case 'secondary':
        return 'bg-gradient-to-r from-gray-600 to-gray-700 hover:from-gray-700 hover:to-gray-800 text-white';
      case 'success':
        return 'bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-700 hover:to-emerald-700 text-white';
      case 'danger':
        return 'bg-gradient-to-r from-red-600 to-pink-600 hover:from-red-700 hover:to-pink-700 text-white';
      case 'lottery':
        return 'bg-gradient-to-r from-gold-500 to-gold-600 hover:from-gold-600 hover:to-gold-700 text-black font-bold';
      default:
        return 'bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white';
    }
  };

  return (
    <motion.div className="relative inline-block">
      <motion.div
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        animate={isPressed ? { scale: 0.95 } : { scale: 1 }}
        transition={{ type: "spring", stiffness: 400, damping: 17 }}
      >
        <Button
          onClick={handleClick}
          disabled={disabled}
          className={`
            relative overflow-hidden transition-all duration-300
            ${getVariantClasses()}
            ${glowEffect && !disabled ? 'shadow-lg hover:shadow-xl hover:shadow-current/25' : ''}
            ${className}
          `}

          {...props}
        >
          <motion.div
            className="relative z-10 flex items-center justify-center gap-2"
            animate={isPressed ? { y: 1 } : { y: 0 }}
          >
            {children}
          </motion.div>

          {glowEffect && !disabled && (
            <motion.div
              className="absolute inset-0 bg-gradient-to-r from-white/20 via-white/10 to-white/20"
              initial={{ x: '-100%' }}
              animate={{ x: '100%' }}
              transition={{ 
                repeat: Infinity, 
                duration: 2, 
                ease: "easeInOut",
                repeatDelay: 3 
              }}
            />
          )}
        </Button>
      </motion.div>

      <AnimatePresence>
        {showSparkles && (
          <motion.div
            className="absolute inset-0 pointer-events-none"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
          >
            {[...Array(6)].map((_, i) => (
              <motion.div
                key={i}
                className="absolute"
                style={{
                  left: `${20 + (i * 10)}%`,
                  top: `${20 + (i * 10)}%`,
                }}
                initial={{ scale: 0, rotate: 0 }}
                animate={{ 
                  scale: [0, 1, 0],
                  rotate: [0, 180, 360],
                  y: [0, -20, -40],
                  x: [0, Math.random() * 20 - 10, Math.random() * 40 - 20]
                }}
                transition={{ duration: 1, delay: i * 0.1 }}
              >
                <Sparkles className="w-3 h-3 text-gold-400" />
              </motion.div>
            ))}
          </motion.div>
        )}
      </AnimatePresence>
    </motion.div>
  );
}

export function PulseButton({ children, className = '', ...props }: Omit<ButtonProps, 'variant'>) {
  return (
    <motion.div
      animate={{
        scale: [1, 1.05, 1],
        opacity: [0.8, 1, 0.8],
      }}
      transition={{
        duration: 2,
        repeat: Infinity,
        ease: "easeInOut"
      }}
    >
      <AnimatedButton
        variant="lottery"
        glowEffect
        sparkleEffect
        soundType="lottery"
        className={`shadow-lg shadow-gold-500/25 ${className}`}
        {...props}
      >
        <Zap className="w-4 h-4 mr-2" />
        {children}
      </AnimatedButton>
    </motion.div>
  );
}

