import React, { useState, useEffect } from 'react';
import Confetti from 'react-confetti';
import { motion, AnimatePresence } from 'framer-motion';
import { useSound } from './sound-manager';

interface ConfettiCelebrationProps {
  trigger: boolean;
  duration?: number;
  intensity?: 'low' | 'medium' | 'high';
  colors?: string[];
  onComplete?: () => void;
}

export function ConfettiCelebration({
  trigger,
  duration = 3000,
  intensity = 'medium',
  colors = ['#FFD700', '#FFA500', '#FF69B4', '#9370DB', '#20B2AA', '#FF6347'],
  onComplete
}: ConfettiCelebrationProps) {
  const [showConfetti, setShowConfetti] = useState(false);
  const [windowDimensions, setWindowDimensions] = useState({
    width: window.innerWidth,
    height: window.innerHeight
  });
  const { playSound } = useSound();

  useEffect(() => {
    const handleResize = () => {
      setWindowDimensions({
        width: window.innerWidth,
        height: window.innerHeight
      });
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    if (trigger) {
      setShowConfetti(true);
      playSound('win');

      const timer = setTimeout(() => {
        setShowConfetti(false);
        if (onComplete) onComplete();
      }, duration);

      return () => clearTimeout(timer);
    }
  }, [trigger, duration, playSound]);

  const getParticleCount = () => {
    switch (intensity) {
      case 'low': return 50;
      case 'medium': return 150;
      case 'high': return 300;
      default: return 150;
    }
  };

  return (
    <AnimatePresence>
      {showConfetti && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 pointer-events-none z-50"
        >
          <Confetti
            width={windowDimensions.width}
            height={windowDimensions.height}
            numberOfPieces={getParticleCount()}
            colors={colors}
            gravity={0.3}
            wind={0.05}
            recycle={false}
            run={showConfetti}
          />
        </motion.div>
      )}
    </AnimatePresence>
  );
}

export function WinCelebration({ trigger }: { trigger: boolean }) {
  return (
    <ConfettiCelebration
      trigger={trigger}
      duration={5000}
      intensity="high"
      colors={['#FFD700', '#FFA500', '#FFFF00', '#FFE55C']}
    />
  );
}

client/src/components/error-boundary.tsx.content:
import React from "react";

interface ErrorBoundaryProps {
  children: React.ReactNode;
  fallback?: React.ReactNode;
}

interface ErrorBoundaryState {
  hasError: boolean;
  error?: Error;
}

export class ErrorBoundary extends React.Component<ErrorBoundaryProps, ErrorBoundaryState> {
  constructor(props: ErrorBoundaryProps) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error: Error): ErrorBoundaryState {
    return { hasError: true, error };
  }

  componentDidCatch(error: Error, errorInfo: React.ErrorInfo) {
    console.error("Error boundary caught an error:", error, errorInfo);
    // Prevent the error from propagating further
    if (error.message.includes('util')) {
      // Silence util module externalization errors
      return;
    }
  }

  render() {
    if (this.state.hasError) {
      return this.props.fallback || (
        <div className="p-4 text-center">
          <p className="text-slate-600">Something went wrong. Please refresh the page.</p>
        </div>
      );
    }

    return this.props.children;
  }
}

client/src/components/floating-particles.content:
import React, { useEffect, useRef } from 'react';

interface Particle {
  x: number;
  y: number;
  vx: number;
  vy: number;
  size: number;
  opacity: number;
  color: string;
  life: number;
  maxLife: number;
}

interface FloatingParticlesProps {
  count?: number;
  colors?: string[];
  speed?: number;
  size?: { min: number; max: number };
}

export function FloatingParticles({
  count = 50,
  colors = ['#FFD700', '#FFA500', '#FF69B4', '#9370DB', '#20B2AA'],
  speed = 0.5,
  size = { min: 1, max: 3 }
}: FloatingParticlesProps) {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const particlesRef = useRef<Particle[]>([]);
  const animationRef = useRef<number>();

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    const createParticle = (): Particle => ({
      x: Math.random() * canvas.width,
      y: Math.random() * canvas.height,
      vx: (Math.random() - 0.5) * speed,
      vy: (Math.random() - 0.5) * speed,
      size: Math.random() * (size.max - size.min) + size.min,
      opacity: Math.random() * 0.5 + 0.1,
      color: colors[Math.floor(Math.random() * colors.length)],
      life: 0,
      maxLife: Math.random() * 1000 + 500,
    });

    const initParticles = () => {
      particlesRef.current = Array.from({ length: count }, createParticle);
    };

    const updateParticle = (particle: Particle) => {
      particle.x += particle.vx;
      particle.y += particle.vy;
      particle.life++;

      // Fade in/out effect
      const lifeCycle = particle.life / particle.maxLife;
      if (lifeCycle < 0.1) {
        particle.opacity = (lifeCycle / 0.1) * 0.5;
      } else if (lifeCycle > 0.9) {
        particle.opacity = ((1 - lifeCycle) / 0.1) * 0.5;
      }

      // Wrap around screen
      if (particle.x < 0) particle.x = canvas.width;
      if (particle.x > canvas.width) particle.x = 0;
      if (particle.y < 0) particle.y = canvas.height;
      if (particle.y > canvas.height) particle.y = 0;

      // Respawn if life is over
      if (particle.life >= particle.maxLife) {
        Object.assign(particle, createParticle());
      }
    };

    const drawParticle = (particle: Particle) => {
      ctx.save();
      ctx.globalAlpha = particle.opacity;
      ctx.fillStyle = particle.color;
      ctx.beginPath();
      ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
      ctx.fill();

      // Add glow effect
      ctx.shadowBlur = 10;
      ctx.shadowColor = particle.color;
      ctx.beginPath();
      ctx.arc(particle.x, particle.y, particle.size * 0.5, 0, Math.PI * 2);
      ctx.fill();

      ctx.restore();
    };

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      particlesRef.current.forEach(particle => {
        updateParticle(particle);
        drawParticle(particle);
      });

      animationRef.current = requestAnimationFrame(animate);
    };

    resizeCanvas();
    initParticles();
    animate();

    window.addEventListener('resize', resizeCanvas);

    return () => {
      window.removeEventListener('resize', resizeCanvas);
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [count, colors, speed, size]);

  return (
    <canvas
      ref={canvasRef}
      className="fixed inset-0 pointer-events-none z-0"
      style={{ background: 'transparent' }}
    />
  );
}

export function GoldParticles() {
  return (
    <FloatingParticles
      count={30}
      colors={['#FFD700', '#FFA500', '#FFFF00']}
      speed={0.3}
      size={{ min: 1, max: 2 }}
    />
  );
}

export function LotteryParticles() {
  return (
    <FloatingParticles
      count={40}
      colors={['#FFD700', '#FF69B4', '#9370DB', '#20B2AA', '#FF6347']}
      speed={0.4}
      size={{ min: 2, max: 4 }}
    />
  );
}

