import React, { createContext, useContext, useEffect, useState } from 'react';
import { Howl, Howler } from 'howler';
import { Volume2, VolumeX } from 'lucide-react';
import { Button } from '@/components/ui/button';

interface SoundContextType {
  playSound: (soundType: SoundType) => void;
  isMuted: boolean;
  toggleMute: () => void;
  setVolume: (volume: number) => void;
}

type SoundType = 'click' | 'success' | 'error' | 'lottery' | 'coin' | 'win' | 'background';

const SoundContext = createContext<SoundContextType | undefined>(undefined);

// Sound URLs (using data URLs for embedded sounds)
const SOUNDS = {
  click: 'data:audio/wav;base64,',
  success: 'data:audio/wav;base64,',
  error: 'data:audio/wav;base64,',
  lottery: 'data:audio/wav;base64,',
  coin: 'data:audio/wav;base64,',
  win: 'data:audio/wav;base64,'
};

const soundInstances: Record<SoundType, Howl> = {} as Record<SoundType, Howl>;

export function SoundProvider({ children }: { children: React.ReactNode }) {
  const [isMuted, setIsMuted] = useState(() => {
    return localStorage.getItem('soundMuted') === 'true';
  });

  useEffect(() => {
    // Initialize all sounds
    Object.entries(SOUNDS).forEach(([key, src]) => {
      soundInstances[key as SoundType] = new Howl({
        src: [src],
        volume: 0.3,
        preload: true,
      });
    });

    // Set initial mute state
    Howler.mute(isMuted);
  }, [isMuted]);

  const playSound = (soundType: SoundType) => {
    if (!isMuted && soundInstances[soundType]) {
      soundInstances[soundType].play();
    }
  };

  const toggleMute = () => {
    const newMutedState = !isMuted;
    setIsMuted(newMutedState);
    Howler.mute(newMutedState);
    localStorage.setItem('soundMuted', newMutedState.toString());
  };

  const setVolume = (volume: number) => {
    Howler.volume(volume);
  };

  return (
    <SoundContext.Provider value={{ playSound, isMuted, toggleMute, setVolume }}>
      {children}
    </SoundContext.Provider>
  );
}

export function useSound() {
  const context = useContext(SoundContext);
  if (!context) {
    throw new Error('useSound must be used within a SoundProvider');
  }
  return context;
}

export function SoundToggle() {
  const { isMuted, toggleMute } = useSound();

  return (
    <Button
      variant="ghost"
      size="sm"
      onClick={toggleMute}
      className="fixed top-4 right-4 z-50 bg-black/20 backdrop-blur-sm border border-gold-400/30"
      data-testid="button-toggle-sound"
    >
      {isMuted ? (
        <VolumeX className="w-4 h-4 text-gold-400" />
      ) : (
        <Volume2 className="w-4 h-4 text-gold-400" />
      )}
    </Button>
  );
}

client/src/components/wallet-connector.t.content:
import React, { useState, useEffect } from 'react';
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Wallet, Unlink, Coins } from "lucide-react";
import Web3Service from "@/lib/web3";

interface WalletConnectorProps {
  onWalletChange?: (address: string | null) => void;
}

export const WalletConnector: React.FC<WalletConnectorProps> = ({
  onWalletChange
}) => {
  const [walletAddress, setWalletAddress] = useState<string | null>(null);
  const [tokenBalance, setTokenBalance] = useState<string>('0');
  const [isConnecting, setIsConnecting] = useState(false);
  const [isDisconnecting, setIsDisconnecting] = useState(false);

  // Check wallet connection status on mount
  useEffect(() => {
    const address = Web3Service.getWalletAddress();
    setWalletAddress(address);
    if (address) {
      updateTokenBalance();
    }
  }, []);

  const updateTokenBalance = async () => {
    try {
      const balance = await Web3Service.getTokenBalance();
      setTokenBalance(balance);
    } catch (error) {
      console.warn('Failed to update token balance:', error);
    }
  };

  const handleConnect = async () => {
    setIsConnecting(true);
    try {
      const address = await Web3Service.connectWallet();
      setWalletAddress(address);
      onWalletChange?.(address);

      if (address) {
        await updateTokenBalance();
      }
    } catch (error) {
      console.warn('Wallet connection failed:', error);
    } finally {
      setIsConnecting(false);
    }
  };

  const handleDisconnect = async () => {
    setIsDisconnecting(true);
    try {
      await Web3Service.disconnectWallet();
      setWalletAddress(null);
      setTokenBalance('0');
      onWalletChange?.(null);
    } catch (error) {
      console.warn('Wallet disconnection failed:', error);
    } finally {
      setIsDisconnecting(false);
    }
  };

  const formatAddress = (address: string) => {
    return `${address.slice(0, 6)}...${address.slice(-4)}`;
  };

  return (
    <Card className="w-full max-w-sm border-2 border-adventure-teal shadow-lg bg-gradient-to-r from-adventure-teal/5 to-ocean-pulse/5">
      <CardHeader className="pb-3 bg-gradient-to-r from-adventure-teal/10 to-ocean-pulse/10">
        <CardTitle className="flex items-center gap-2 text-lg font-bold text-adventure-teal">
          <Wallet className="h-5 w-5" />
          Web3 Wallet
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-3">
        {!walletAddress ? (
          <div className="space-y-3">
            <p className="text-sm text-slate-600">
              Connect your wallet to participate in blockchain lotteries and earn tokens.
            </p>
            <Button
              onClick={handleConnect}
              disabled={isConnecting}
              className="w-full btn-lottery gap-2"
              data-testid="connect-wallet-button"
            >
              <Wallet className="h-4 w-4" />
              {isConnecting ? 'Connecting...' : 'Connect Wallet'}
            </Button>
          </div>
        ) : (
          <div className="space-y-3">
            <div className="p-3 bg-slate-50 rounded-lg">
              <div className="font-medium text-sm mb-1">Connected Wallet</div>
              <Badge variant="secondary" className="bg-adventure-teal text-white">
                {formatAddress(walletAddress)}
              </Badge>
            </div>

            <div className="p-3 bg-slate-50 rounded-lg">
              <div className="flex items-center justify-between">
                <div className="font-medium text-sm">Token Balance</div>
                <div className="flex items-center gap-1">
                  <Coins className="h-4 w-4 text-lottery-gold" />
                  <span className="font-bold text-lottery-gold">
                    {Web3Service.formatTokenAmount(tokenBalance)}
                  </span>
                </div>
              </div>
            </div>

            <Button
              onClick={handleDisconnect}
              disabled={isDisconnecting}
              variant="outline"
              className="w-full gap-2"
              data-testid="disconnect-wallet-button"
            >
              <Unlink className="h-4 w-4" />
              {isDisconnecting ? 'Disconnecting...' : 'Disconnect'}
            </Button>
          </div>
        )}

        <div className="p-3 bg-blue-50 border border-blue-200 rounded-lg text-sm">
          <div className="font-medium text-blue-800 mb-1">‚ÑπÔ∏è DApp Features</div>
          <div className="text-blue-700">
            {walletAddress 
              ? 'Your lottery tickets will be stored on the blockchain as NFTs.'
              : 'Connect to unlock blockchain lottery tickets and token rewards.'
            }
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

export default WalletConnector;

client/src/hooks/use-simple-toast.ts.content:
import { useState, useCallback } from "react";

interface Toast {
  id: string;
  title?: string;
  description?: string;
  variant?: "default" | "destructive";
}

let toastId = 0;

export function useSimpleToast() {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const addToast = useCallback((toast: Omit<Toast, "id">) => {
    const id = `toast-${++toastId}`;
    const newToast = { id, ...toast };

    setToasts(prev => [...prev, newToast]);

    // Auto-remove after 3 seconds
    setTimeout(() => {
      removeToast(id);
    }, 3000);

    return id;
  }, []);

  const removeToast = useCallback((id: string) => {
    setToasts(prev => prev.filter(toast => toast.id !== id));
  }, []);

  const toast = useCallback((options: Omit<Toast, "id">) => {
    return addToast(options);
  }, [addToast]);

  return {
    toasts,
    toast,
    removeToast,
  };
}

