import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Link } from 'wouter';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { ArrowLeft, Star, Coins, ShoppingCart, CreditCard, Zap } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { loadStripe } from '@stripe/stripe-js';
import { Elements, PaymentElement, useStripe, useElements } from '@stripe/react-stripe-js';
import type { TokenPack } from "@shared/schema";

// Initialize Stripe
if (!import.meta.env.VITE_STRIPE_PUBLIC_KEY) {
  throw new Error('Missing required Stripe key: VITE_STRIPE_PUBLIC_KEY');
}
const stripePromise = loadStripe(import.meta.env.VITE_STRIPE_PUBLIC_KEY);

interface CheckoutFormProps {
  tokenPack: TokenPack;
  onSuccess: () => void;
  onCancel: () => void;
}

const CheckoutForm: React.FC<CheckoutFormProps> = ({ tokenPack, onSuccess, onCancel }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [isProcessing, setIsProcessing] = useState(false);
  const [errorMessage, setErrorMessage] = useState<string | null>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!stripe || !elements) return;

    setIsProcessing(true);
    setErrorMessage(null);

    try {
      const { error } = await stripe.confirmPayment({
        elements,
        confirmParams: {
          return_url: `${window.location.origin}/token-shop?success=true`,
        },
      });

      if (error) {
        setErrorMessage(error.message || 'Payment failed');
      } else {
        onSuccess();
      }
    } catch (err) {
      setErrorMessage('An unexpected error occurred');
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="max-w-md mx-auto">
      <Card className="border-2 border-lottery-gold shadow-lg">
        <CardHeader className="bg-gradient-to-r from-lottery-gold/10 to-lottery-orange/10">
          <CardTitle className="text-center">
            Complete Your Purchase
          </CardTitle>
          <div className="text-center space-y-2">
            <div className="text-2xl font-bold text-lottery-gold">
              {tokenPack.tokenAmount} Tokens
            </div>
            <div className="text-lg text-slate-600">
              ${tokenPack.priceUsd} USD
            </div>
          </div>
        </CardHeader>
        <CardContent className="space-y-4">
          <form onSubmit={handleSubmit} className="space-y-4">
            <PaymentElement />

            {errorMessage && (
              <div className="p-3 bg-red-50 border border-red-200 rounded-lg text-red-700 text-sm">
                {errorMessage}
              </div>
            )}

            <div className="flex gap-3">
              <Button
                type="button"
                variant="outline"
                onClick={onCancel}
                className="flex-1"
                disabled={isProcessing}
                data-testid="cancel-payment-button"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={!stripe || isProcessing}
                className="flex-1 btn-lottery"
                data-testid="confirm-payment-button"
              >
                {isProcessing ? 'Processing...' : `Pay $${tokenPack.priceUsd}`}
              </Button>
            </div>
          </form>
        </CardContent>
      </Card>
    </div>
  );
};

export default function TokenShop() {
  const queryClient = useQueryClient();
  const [selectedPack, setSelectedPack] = useState<TokenPack | null>(null);
  const [clientSecret, setClientSecret] = useState<string>('');
  const [showCheckout, setShowCheckout] = useState(false);

  // Fetch token packs
  const { data: tokenPacks, isLoading } = useQuery<TokenPack[]>({
    queryKey: ['/api/token-packs'],
    queryFn: () => apiRequest('/api/token-packs').then(res => res.json()),
  });

  // Create payment intent mutation
  const createPaymentMutation = useMutation({
    mutationFn: async (tokenPackId: string) => {
      const response = await apiRequest('/api/create-token-payment-intent', {
        method: 'POST',
        body: { tokenPackId, userId: 'sample-user' }
      });
      const data = await response.json();
      if (!response.ok) {
        throw new Error(data.message || 'Payment creation failed');
      }
      return data;
    },
    onSuccess: (data) => {
      setClientSecret(data.clientSecret);
      setShowCheckout(true);
    },
    onError: (error: any) => {
      console.error('Payment creation failed:', error);
      // Show user-friendly error message
      alert(`Payment system error: ${error.message || 'Unable to create payment. Please try again later.'}`);
    }
  });

  const handlePurchase = (tokenPack: TokenPack) => {
    setSelectedPack(tokenPack);
    createPaymentMutation.mutate(tokenPack.id);
  };

  const handlePaymentSuccess = () => {
    setShowCheckout(false);
    setSelectedPack(null);
    setClientSecret('');
    queryClient.invalidateQueries({ queryKey: ['/api/users'] });
  };

  const handlePaymentCancel = () => {
    setShowCheckout(false);
    setSelectedPack(null);
    setClientSecret('');
  };

  const getPackIcon = (tokenAmount: number) => {
    if (tokenAmount === 54) return <Coins className="h-8 w-8 text-adventure-teal" />;
    if (tokenAmount === 189) return <Star className="h-8 w-8 text-lottery-gold" />;
    return <Zap className="h-8 w-8 text-lottery-purple" />;
  };

  const getPackGradient = (tokenAmount: number) => {
    if (tokenAmount === 54) return "from-adventure-teal/10 to-adventure-cyan/10";
    if (tokenAmount === 189) return "from-lottery-gold/10 to-lottery-orange/10";
    return "from-lottery-purple/10 to-lottery-pink/10";
  };

  if (showCheckout && selectedPack && clientSecret) {
    return (
      <div className="min-h-screen bg-gradient-to-b from-slate-50 to-slate-100 p-4">
        <div className="max-w-6xl mx-auto py-8">
          <div className="text-center mb-8">
            <Link href="/token-shop">
              <Button variant="ghost" className="mb-4">
                <ArrowLeft className="h-4 w-4 mr-2" />
                Back to Token Shop
              </Button>
            </Link>
          </div>

          <Elements stripe={stripePromise} options={{ clientSecret }}>
            <CheckoutForm
              tokenPack={selectedPack}
              onSuccess={handlePaymentSuccess}
              onCancel={handlePaymentCancel}
            />
          </Elements>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-slate-50 to-slate-100">
      {/* Header */}
      <div className="bg-gradient-to-r from-lottery-gold via-lottery-orange to-lottery-purple text-white py-16">
        <div className="max-w-6xl mx-auto px-4 text-center">
          <Link href="/dashboard">
            <Button variant="ghost" className="mb-4 text-white hover:bg-white/20">
              <ArrowLeft className="h-4 w-4 mr-2" />
              Back to Dashboard
            </Button>
          </Link>

          <h1 className="text-4xl md:text-5xl font-bold mb-4">
            Token Shop
          </h1>
          <p className="text-xl text-white/90 max-w-2xl mx-auto">
            Purchase token packs to participate in exciting lottery drawings for amazing travel prizes
          </p>
        </div>
      </div>

      {/* Content */}
      <div className="max-w-6xl mx-auto p-4 py-12">
        {isLoading ? (
          <div className="text-center py-12">
            <div className="animate-spin w-8 h-8 border-4 border-lottery-gold border-t-transparent rounded-full mx-auto"></div>
            <p className="mt-4 text-slate-600">Loading token packs...</p>
          </div>
        ) : (
          <div className="grid md:grid-cols-3 gap-6">
            {tokenPacks?.map((pack) => (
              <Card 
                key={pack.id} 
                className={`relative border-2 hover:shadow-lg transition-all duration-300 ${
                  pack.popularBadge ? 'border-lottery-gold scale-105' : 'border-slate-200'
                }`}
              >
                {pack.popularBadge && (
                  <div className="absolute -top-3 left-1/2 transform -translate-x-1/2">
                    <Badge className="bg-lottery-gold text-white px-4 py-1">
                      <Star className="h-3 w-3 mr-1" />
                      Most Popular
                    </Badge>
                  </div>
                )}

                <CardHeader className={`bg-gradient-to-r ${getPackGradient(pack.tokenAmount)} text-center`}>
                  <div className="flex justify-center mb-3">
                    {getPackIcon(pack.tokenAmount)}
                  </div>
                  <CardTitle className="text-xl font-bold">
                    {pack.name}
                  </CardTitle>
                  <p className="text-slate-600 text-sm">
                    {pack.description}
                  </p>
                </CardHeader>

                <CardContent className="text-center space-y-4">
                  <div className="py-4">
                    <div className="text-3xl font-bold text-lottery-gold mb-2">
                      {pack.tokenAmount}
                    </div>
                    <div className="text-lg text-slate-600">
                      Tokens
                    </div>
                  </div>

                  <div className="border-t pt-4">
                    <div className="text-2xl font-bold text-slate-900 mb-4">
                      ${pack.priceUsd} USD
                    </div>
                    <Button
                      onClick={() => handlePurchase(pack)}
                      disabled={createPaymentMutation.isPending}
                      className="w-full btn-lottery gap-2"
                      data-testid={`buy-token-pack-${pack.tokenAmount}`}
                    >
                      <ShoppingCart className="h-4 w-4" />
                      {createPaymentMutation.isPending ? 'Processing...' : 'Purchase Now'}
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        {/* Info Section */}
        <div className="mt-12 bg-blue-50 border border-blue-200 rounded-lg p-6">
          <h3 className="text-lg font-bold text-blue-900 mb-3">
            <CreditCard className="h-5 w-5 inline mr-2" />
            How Token Purchasing Works
          </h3>
          <div className="text-blue-800 space-y-2">
            <p>‚Ä¢ Tokens are used to purchase lottery tickets for amazing travel prizes</p>
            <p>‚Ä¢ All payments are securely processed through Stripe</p>
            <p>‚Ä¢ Tokens are instantly added to your account after successful payment</p>
            <p>‚Ä¢ Your tokens never expire and can be used across all lotteries</p>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/verification-demo.tsx.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Progress } from "@/components/ui/progress";
import { Clock, Camera, FileText, CheckCircle, AlertCircle, Award, User } from "lucide-react";

// Simulated verification methods for demonstration
export default function VerificationDemo() {
  const [autoProgress, setAutoProgress] = useState(0);
  const [proofData, setProofData] = useState({ photo: "", text: "" });
  const [timeElapsed, setTimeElapsed] = useState(0);
  const [verificationStates, setVerificationStates] = useState({
    auto: "ready", // ready, processing, completed
    proof: "ready", // ready, submitted, verified
    manual: "ready", // ready, submitted, under_review, approved/rejected
    time: "ready" // ready, in_progress, completed
  });

  // Auto verification simulation
  const handleAutoVerification = () => {
    setVerificationStates(prev => ({ ...prev, auto: "processing" }));
    setAutoProgress(0);

    const interval = setInterval(() => {
      setAutoProgress(prev => {
        if (prev >= 100) {
          clearInterval(interval);
          setVerificationStates(curr => ({ ...curr, auto: "completed" }));
          return 100;
        }
        return prev + 20;
      });
    }, 200);
  };

  // Proof-based verification simulation
  const handleProofSubmission = () => {
    if (!proofData.photo && !proofData.text) {
      alert("Please provide either a photo URL or text description");
      return;
    }

    setVerificationStates(prev => ({ ...prev, proof: "submitted" }));

    // Simulate automatic verification of proof
    setTimeout(() => {
      setVerificationStates(prev => ({ ...prev, proof: "verified" }));
    }, 1500);
  };

  // Manual verification simulation
  const handleManualSubmission = () => {
    setVerificationStates(prev => ({ ...prev, manual: "submitted" }));

    setTimeout(() => {
      setVerificationStates(prev => ({ ...prev, manual: "under_review" }));
    }, 1000);
  };

  // Simulate admin approval
  const handleAdminApproval = (approved: boolean) => {
    setVerificationStates(prev => ({ 
      ...prev, 
      manual: approved ? "approved" : "rejected" 
    }));
  };

  // Time-based verification simulation
  const handleTimeBasedStart = () => {
    setVerificationStates(prev => ({ ...prev, time: "in_progress" }));
    setTimeElapsed(0);

    const timer = setInterval(() => {
      setTimeElapsed(prev => {
        if (prev >= 120) { // 2 minutes simulation
          clearInterval(timer);
          setVerificationStates(curr => ({ ...curr, time: "completed" }));
          return 120;
        }
        return prev + 1;
      });
    }, 50); // Fast simulation
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "ready":
        return <Badge variant="secondary">Ready</Badge>;
      case "processing":
      case "in_progress":
      case "submitted":
        return <Badge variant="default">In Progress</Badge>;
      case "under_review":
        return <Badge variant="outline">Under Review</Badge>;
      case "completed":
      case "verified":
      case "approved":
        return <Badge variant="default" className="bg-green-500">Completed</Badge>;
      case "rejected":
        return <Badge variant="destructive">Rejected</Badge>;
      default:
        return <Badge variant="secondary">{status}</Badge>;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50 py-8">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="mb-8 text-center">
          <h1 className="text-4xl font-bold text-slate-900 mb-2">Mission Verification System Demo</h1>
          <p className="text-lg text-slate-600">
            Interactive demonstration of different verification methods for mission compliance
          </p>
        </div>

        <div className="grid lg:grid-cols-2 gap-6">
          {/* Auto Verification */}
          <Card className="border-green-200">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Award className="w-5 h-5 text-green-500" />
                Auto Verification
                {getStatusBadge(verificationStates.auto)}
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <p className="text-sm text-slate-600">
                Missions are automatically verified after completion. May include optional delay for realism.
              </p>

              {verificationStates.auto === "processing" && (
                <div className="space-y-2">
                  <div className="flex justify-between text-sm">
                    <span>Processing...</span>
                    <span>{autoProgress}%</span>
                  </div>
                  <Progress value={autoProgress} className="w-full" />
                </div>
              )}

              <div className="flex gap-2">
                <Button 
                  onClick={handleAutoVerification}
                  disabled={verificationStates.auto !== "ready"}
                  className="bg-green-500 hover:bg-green-600"
                  data-testid="button-auto-verification"
                >
                  {verificationStates.auto === "ready" ? "Complete Mission" : 
                   verificationStates.auto === "processing" ? "Processing..." : "‚úì Completed"}
                </Button>

                {verificationStates.auto === "completed" && (
                  <Button 
                    variant="outline" 
                    onClick={() => setVerificationStates(prev => ({ ...prev, auto: "ready" }))}
                    data-testid="button-auto-reset"
                  >
                    Reset
                  </Button>
                )}
              </div>

              {verificationStates.auto === "completed" && (
                <div className="flex items-center gap-2 text-green-600 bg-green-50 p-3 rounded">
                  <CheckCircle className="w-4 h-4" />
                  <span className="text-sm font-medium">Mission completed! 150 tokens awarded</span>
                </div>
              )}
            </CardContent>
          </Card>

          {/* Proof Required Verification */}
          <Card className="border-blue-200">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Camera className="w-5 h-5 text-blue-500" />
                Proof Required
                {getStatusBadge(verificationStates.proof)}
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <p className="text-sm text-slate-600">
                Submit photo or text evidence to verify mission completion.
              </p>

              <div className="space-y-3">
                <div>
                  <Label htmlFor="photo-url">Photo Evidence (URL)</Label>
                  <Input
                    id="photo-url"
                    type="url"
                    placeholder="https://example.com/photo.jpg"
                    value={proofData.photo}
                    onChange={(e) => setProofData(prev => ({ ...prev, photo: e.target.value }))}
                    disabled={verificationStates.proof !== "ready"}
                  />
                </div>

                <div>
                  <Label htmlFor="text-proof">Text Description</Label>
                  <Textarea
                    id="text-proof"
                    placeholder="Describe your experience..."
                    value={proofData.text}
                    onChange={(e) => setProofData(prev => ({ ...prev, text: e.target.value }))}
                    disabled={verificationStates.proof !== "ready"}
                    rows={3}
                  />
                </div>
              </div>

              <div className="flex gap-2">
                <Button 
                  onClick={handleProofSubmission}
                  disabled={verificationStates.proof !== "ready"}
                  className="bg-blue-500 hover:bg-blue-600"
                >
                  {verificationStates.proof === "ready" ? "Submit Proof" : 
                   verificationStates.proof === "submitted" ? "Verifying..." : "‚úì Verified"}
                </Button>

                {verificationStates.proof === "verified" && (
                  <Button 
                    variant="outline" 
                    onClick={() => {
                      setVerificationStates(prev => ({ ...prev, proof: "ready" }));
                      setProofData({ photo: "", text: "" });
                    }}
                  >
                    Reset
                  </Button>
                )}
              </div>

              {verificationStates.proof === "verified" && (
                <div className="flex items-center gap-2 text-blue-600 bg-blue-50 p-3 rounded">
                  <CheckCircle className="w-4 h-4" />
                  <span className="text-sm font-medium">Proof verified! 200 tokens awarded</span>
                </div>
              )}
            </CardContent>
          </Card>

          {/* Manual Review Verification */}
          <Card className="border-amber-200">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <User className="w-5 h-5 text-amber-500" />
                Manual Review
                {getStatusBadge(verificationStates.manual)}
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <p className="text-sm text-slate-600">
                Requires admin review and approval before tokens are awarded.
              </p>

              <div className="flex gap-2">
                <Button 
                  onClick={handleManualSubmission}
                  disabled={verificationStates.manual !== "ready"}
                  className="bg-amber-500 hover:bg-amber-600"
                >
                  {verificationStates.manual === "ready" ? "Submit for Review" : 
                   verificationStates.manual === "submitted" ? "Submitting..." : 
                   verificationStates.manual === "under_review" ? "Under Review" : 
                   verificationStates.manual === "approved" ? "‚úì Approved" : "‚úó Rejected"}
                </Button>

                {verificationStates.manual === "under_review" && (
                  <>
                    <Button 
                      onClick={() => handleAdminApproval(true)}
                      size="sm"
                      className="bg-green-500 hover:bg-green-600"
                    >
                      Admin: Approve
                    </Button>
                    <Button 
                      onClick={() => handleAdminApproval(false)}
                      size="sm"
                      variant="destructive"
                    >
                      Admin: Reject
                    </Button>
                  </>
                )}

                {(verificationStates.manual === "approved" || verificationStates.manual === "rejected") && (
                  <Button 
                    variant="outline" 
                    onClick={() => setVerificationStates(prev => ({ ...prev, manual: "ready" }))}
                  >
                    Reset
                  </Button>
                )}
              </div>

              {verificationStates.manual === "approved" && (
                <div className="flex items-center gap-2 text-green-600 bg-green-50 p-3 rounded">
                  <CheckCircle className="w-4 h-4" />
                  <span className="text-sm font-medium">Mission approved! 180 tokens awarded</span>
                </div>
              )}

              {verificationStates.manual === "rejected" && (
                <div className="flex items-center gap-2 text-red-600 bg-red-50 p-3 rounded">
                  <AlertCircle className="w-4 h-4" />
                  <span className="text-sm font-medium">Mission rejected. No tokens awarded.</span>
                </div>
              )}
            </CardContent>
          </Card>

          {/* Time-based Verification */}
          <Card className="border-purple-200">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Clock className="w-5 h-5 text-purple-500" />
                Time-based Verification
                {getStatusBadge(verificationStates.time)}
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <p className="text-sm text-slate-600">
                Requires minimum time duration to complete. Mission starts when initiated.
              </p>

              {verificationStates.time === "in_progress" && (
                <div className="space-y-2">
                  <div className="flex justify-between text-sm">
                    <span>Time elapsed:</span>
                    <span>{Math.floor(timeElapsed / 60)}:{(timeElapsed % 60).toString().padStart(2, '0')}</span>
                  </div>
                  <Progress value={(timeElapsed / 120) * 100} className="w-full" />
                  <p className="text-xs text-slate-500">Minimum required: 2:00</p>
                </div>
              )}

              <div className="flex gap-2">
                <Button 
                  onClick={handleTimeBasedStart}
                  disabled={verificationStates.time !== "ready"}
                  className="bg-purple-500 hover:bg-purple-600"
                >
                  {verificationStates.time === "ready" ? "Start Mission" : 
                   verificationStates.time === "in_progress" ? "In Progress..." : "‚úì Completed"}
                </Button>

                {verificationStates.time === "completed" && (
                  <Button 
                    variant="outline" 
                    onClick={() => {
                      setVerificationStates(prev => ({ ...prev, time: "ready" }));
                      setTimeElapsed(0);
                    }}
                  >
                    Reset
                  </Button>
                )}
              </div>

              {verificationStates.time === "completed" && (
                <div className="flex items-center gap-2 text-purple-600 bg-purple-50 p-3 rounded">
                  <CheckCircle className="w-4 h-4" />
                  <span className="text-sm font-medium">Time requirement met! 300 tokens awarded</span>
                </div>
              )}
            </CardContent>
          </Card>
        </div>

        {/* Summary */}
        <Card className="mt-8 bg-slate-50">
          <CardHeader>
            <CardTitle>Verification Summary</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid md:grid-cols-4 gap-4 text-sm">
              <div className="text-center">
                <div className="font-medium text-green-600">Auto Verification</div>
                <div className="text-xs text-slate-600">Instant completion with optional delay</div>
              </div>
              <div className="text-center">
                <div className="font-medium text-blue-600">Proof Required</div>
                <div className="text-xs text-slate-600">Photo/text evidence validation</div>
              </div>
              <div className="text-center">
                <div className="font-medium text-amber-600">Manual Review</div>
                <div className="text-xs text-slate-600">Human administrator approval</div>
              </div>
              <div className="text-center">
                <div className="font-medium text-purple-600">Time-based</div>
                <div className="text-xs text-slate-600">Minimum duration requirement</div>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/terms-of-service.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { CheckCircle, AlertTriangle, FileText, Shield, Globe, Coins } from "lucide-react";

export default function TermsOfService() {
  const [acceptedSections, setAcceptedSections] = useState<string[]>([]);

  const toggleSection = (sectionId: string) => {
    setAcceptedSections(prev => 
      prev.includes(sectionId) 
        ? prev.filter(id => id !== sectionId)
        : [...prev, sectionId]
    );
  };

  const sections = [
    {
      id: "platform-overview",
      title: "Platform Overview & Purpose",
      icon: <Globe className="h-5 w-5" />,
      content: [
        "TravelLotto is a blockchain-powered travel lottery platform that combines digital gaming mechanics with authentic travel experiences.",
        "Users participate in themed missions, lotteries, and challenges to earn tokens and NFTs redeemable for real travel packages.",
        "All lottery draws are conducted transparently using blockchain technology to ensure fairness and verifiability.",
        "The platform operates internationally and complies with applicable laws in jurisdictions where it operates."
      ]
    },
    {
      id: "user-eligibility",
      title: "User Eligibility & Account Requirements",
      icon: <Shield className="h-5 w-5" />,
      content: [
        "Users must be 18 years or older to participate in lottery activities.",
        "Account registration requires valid contact information and email verification.",
        "Users are responsible for maintaining the security of their wallet connections and private keys.",
        "Multi-account creation to circumvent platform limits is strictly prohibited.",
        "Users must comply with local laws regarding online gaming and lottery participation."
      ]
    },
    {
      id: "token-economics",
      title: "Token Economics & Digital Assets",
      icon: <Coins className="h-5 w-5" />,
      content: [
        "Platform tokens are utility tokens used exclusively for purchasing lottery tickets and accessing platform features.",
        "Token purchases are final and non-refundable except as required by applicable law.",
        "Token balances do not expire but may be subject to inactivity policies after 24 months.",
        "NFT rewards represent digital collectibles and may include redemption rights for physical experiences.",
        "All blockchain transactions are recorded permanently and cannot be reversed."
      ]
    },
    {
      id: "lottery-operations",
      title: "Lottery Operations & Fair Play",
      icon: <FileText className="h-5 w-5" />,
      content: [
        "All lottery draws are conducted using cryptographically secure random number generation.",
        "Draw dates and times are published in advance and cannot be modified once tickets are sold.",
        "Maximum ticket limits per user may apply to ensure fair participation.",
        "Prize distribution follows the published prize structure with no hidden deductions.",
        "Winners are notified within 48 hours and have 30 days to claim prizes."
      ]
    },
    {
      id: "travel-redemption",
      title: "Travel Prize Redemption & Fulfillment",
      icon: <CheckCircle className="h-5 w-5" />,
      content: [
        "Travel prizes must be redeemed within 12 months of winning unless otherwise specified.",
        "Prize packages include specified accommodations, transportation, and experiences as detailed.",
        "Winners are responsible for obtaining necessary travel documents (passport, visas, etc.).",
        "Travel dates are subject to availability and may require advance booking.",
        "Prize packages cannot be exchanged for cash value except where legally required."
      ]
    },
    {
      id: "mission-verification",
      title: "Mission Verification & Compliance",
      icon: <AlertTriangle className="h-5 w-5" />,
      content: [
        "Mission completion requires authentic verification according to specified criteria.",
        "Photo and text submissions must be original content created by the user.",
        "False or fraudulent mission submissions result in immediate account suspension.",
        "Verification processes may include manual review and can take up to 72 hours.",
        "Disputed verifications are reviewed by platform administrators with final decision authority."
      ]
    }
  ];

  const allSectionsAccepted = sections.every(section => acceptedSections.includes(section.id));

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-teal-800">
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          <div className="text-center mb-8">
            <h1 className="text-4xl font-bold text-white mb-4">
              Terms of Service & Operating Conditions
            </h1>
            <p className="text-lg text-gray-200">
              Please review and accept all sections to use the TravelLotto platform
            </p>
            <div className="flex items-center justify-center mt-4 space-x-4">
              <Badge variant="secondary" className="bg-gold/20 text-gold border-gold/30">
                Effective Date: January 2025
              </Badge>
              <Badge variant="secondary" className="bg-teal/20 text-teal border-teal/30">
                Version 1.0
              </Badge>
            </div>
          </div>

          <div className="space-y-6">
            {sections.map((section) => {
              const isAccepted = acceptedSections.includes(section.id);
              return (
                <Card key={section.id} className="bg-white/10 backdrop-blur-sm border-white/20">
                  <CardHeader>
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-3">
                        <div className="text-gold">{section.icon}</div>
                        <CardTitle className="text-white">{section.title}</CardTitle>
                      </div>
                      <Button
                        onClick={() => toggleSection(section.id)}
                        variant={isAccepted ? "default" : "outline"}
                        size="sm"
                        className={isAccepted 
                          ? "bg-emerald-600 hover:bg-emerald-700 text-white" 
                          : "border-white/30 text-white hover:bg-white/10"
                        }
                        data-testid={`accept-${section.id}`}
                      >
                        {isAccepted ? (
                          <>
                            <CheckCircle className="h-4 w-4 mr-2" />
                            Accepted
                          </>
                        ) : (
                          "Accept Section"
                        )}
                      </Button>
                    </div>
                  </CardHeader>
                  <CardContent>
                    <ScrollArea className="h-32">
                      <div className="space-y-2">
                        {section.content.map((item, index) => (
                          <p key={index} className="text-gray-200 text-sm leading-relaxed">
                            ‚Ä¢ {item}
                          </p>
                        ))}
                      </div>
                    </ScrollArea>
                  </CardContent>
                </Card>
              );
            })}
          </div>

          <Separator className="my-8 bg-white/20" />

          <Card className="bg-white/10 backdrop-blur-sm border-white/20">
            <CardHeader>
              <CardTitle className="text-white text-center">Agreement Summary</CardTitle>
              <CardDescription className="text-gray-300 text-center">
                By accepting all sections above, you agree to the complete Terms of Service
              </CardDescription>
            </CardHeader>
            <CardContent className="text-center">
              <div className="mb-4">
                <p className="text-gray-200 mb-2">
                  Sections Accepted: {acceptedSections.length} of {sections.length}
                </p>
                <div className="w-full bg-gray-700 rounded-full h-2">
                  <div 
                    className="bg-gradient-to-r from-gold to-teal h-2 rounded-full transition-all duration-300"
                    style={{ width: `${(acceptedSections.length / sections.length) * 100}%` }}
                  />
                </div>
              </div>

              <Button
                disabled={!allSectionsAccepted}
                className={allSectionsAccepted 
                  ? "bg-gradient-to-r from-gold to-amber-500 hover:from-gold/90 hover:to-amber-500/90 text-black font-semibold px-8 py-3"
                  : "bg-gray-600 text-gray-400 cursor-not-allowed px-8 py-3"
                }
                size="lg"
                data-testid="complete-acceptance"
              >
                {allSectionsAccepted ? (
                  <>
                    <CheckCircle className="h-5 w-5 mr-2" />
                    Complete Agreement
                  </>
                ) : (
                  "Accept All Sections to Continue"
                )}
              </Button>

              {allSectionsAccepted && (
                <p className="text-emerald-400 text-sm mt-3">
                  ‚úì You have accepted all terms and conditions
                </p>
              )}
            </CardContent>
          </Card>

          <div className="mt-8 text-center text-gray-400 text-sm">
            <p>
              For questions about these terms, contact us at legal@travellotto.com
            </p>
            <p className="mt-2">
              These terms may be updated periodically. Users will be notified of material changes.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/profile-dropdown.t.content:
import React from "react";
import { Link } from "wouter";
import { useLanguage } from "@/lib/i18n";
import { Button } from "@/components/ui/button";
import { 
  DropdownMenu, 
  DropdownMenuContent, 
  DropdownMenuItem, 
  DropdownMenuSeparator,
  DropdownMenuTrigger,
  DropdownMenuLabel
} from "@/components/ui/dropdown-menu";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { 
  User, 
  Settings, 
  HelpCircle, 
  LogOut, 
  Crown,
  Coins
} from "lucide-react";
import LanguageSelector from "./language-selector";
import type { User as UserType } from "@shared/schema";

interface ProfileDropdownProps {
  user?: UserType;
}

export default function ProfileDropdown({ user }: ProfileDropdownProps) {
  const { t } = useLanguage();

  const handleLogout = () => {
    // Implementation for logout
    window.location.href = '/api/logout';
  };

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button 
          variant="ghost" 
          className="relative h-10 w-10 rounded-full"
          data-testid="profile-dropdown-trigger"
        >
          <Avatar className="h-10 w-10">
            <AvatarImage src={user?.avatar || undefined} alt={user?.username || 'User'} />
            <AvatarFallback className="bg-gradient-to-r from-purple-500 to-pink-500 text-white">
              {user?.username?.charAt(0).toUpperCase() || 'U'}
            </AvatarFallback>
          </Avatar>
          {user?.level && user.level > 1 && (
            <Badge 
              variant="secondary" 
              className="absolute -top-1 -right-1 h-5 w-5 p-0 flex items-center justify-center text-xs"
            >
              {user.level}
            </Badge>
          )}
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent className="w-64" align="end" forceMount>
        <DropdownMenuLabel className="p-4">
          <div className="flex flex-col space-y-2">
            <div className="flex items-center gap-3">
              <Avatar className="h-12 w-12">
                <AvatarImage src={user?.avatar || undefined} alt={user?.username || 'User'} />
                <AvatarFallback className="bg-gradient-to-r from-purple-500 to-pink-500 text-white text-lg">
                  {user?.username?.charAt(0).toUpperCase() || 'U'}
                </AvatarFallback>
              </Avatar>
              <div className="flex flex-col">
                <p className="font-medium text-sm">{user?.username || 'Explorer'}</p>
                <p className="text-xs text-muted-foreground">{user?.email || 'user@example.com'}</p>
              </div>
            </div>

            {/* User Stats */}
            <div className="flex items-center justify-between pt-2 border-t">
              <div className="flex items-center gap-1 text-xs text-muted-foreground">
                <Coins className="h-3 w-3" />
                <span>{user?.viatorTokens || "0.00"} Viator</span>
              </div>
              {user?.level && (
                <div className="flex items-center gap-1 text-xs text-muted-foreground">
                  <Crown className="h-3 w-3" />
                  <span>{t('level')} {user.level}</span>
                </div>
              )}
            </div>
          </div>
        </DropdownMenuLabel>

        <DropdownMenuSeparator />

        <Link href="/profile">
          <DropdownMenuItem className="cursor-pointer" data-testid="profile-menu-profile">
            <User className="mr-2 h-4 w-4" />
            <span>{t('viewProfile')}</span>
          </DropdownMenuItem>
        </Link>

        <DropdownMenuItem className="cursor-pointer" data-testid="profile-menu-settings">
          <Settings className="mr-2 h-4 w-4" />
          <span>{t('settings')}</span>
        </DropdownMenuItem>

        <DropdownMenuSeparator />

        {/* Language Selector in Dropdown */}
        <div className="px-2 py-1">
          <div className="flex items-center justify-between">
            <span className="text-sm font-medium">{t('language')}</span>
            <LanguageSelector variant="ghost" size="sm" />
          </div>
        </div>

        <DropdownMenuSeparator />

        <DropdownMenuItem className="cursor-pointer" data-testid="profile-menu-help">
          <HelpCircle className="mr-2 h-4 w-4" />
          <span>{t('help')}</span>
        </DropdownMenuItem>

        <DropdownMenuSeparator />

        <DropdownMenuItem 
          className="cursor-pointer text-red-600 focus:text-red-600" 
          onClick={handleLogout}
          data-testid="profile-menu-logout"
        >
          <LogOut className="mr-2 h-4 w-4" />
          <span>{t('logout')}</span>
        </DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

