                                    <div className="flex items-center gap-3">
                                      <Phone className="h-4 w-4 text-slate-500" />
                                      <a href={`tel:${selectedPrize.assignedAgency.phone}`} 
                                         className="text-blue-600 hover:underline">
                                        {selectedPrize.assignedAgency.phone}
                                      </a>
