}

client/src/components/KairosTokenBalance.content:
import { useQuery } from "@tanstack/react-query";
import { Coins } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { useLocation } from "wouter";
import type { User } from "@shared/schema";

interface KairosTokenBalanceProps {
  showConvertButton?: boolean;
  size?: 'sm' | 'md' | 'lg';
  variant?: 'compact' | 'detailed';
}

export function KairosTokenBalance({ 
  showConvertButton = false, 
  size = 'md',
  variant = 'compact' 
}: KairosTokenBalanceProps) {
  const [, setLocation] = useLocation();

  const { data: user, isLoading } = useQuery<User>({
    queryKey: ["/api/users/sample-user"],
  });

  const handleConvertTokens = () => {
    setLocation('/token-management');
  };

  if (isLoading) {
    return (
      <div className="flex items-center gap-2">
        <div className="animate-pulse bg-gray-200 rounded-full h-6 w-16"></div>
      </div>
    );
  }

  const kairosBalance = user?.kairosTokens || 0;
  const raivanBalance = user?.raivanTokens || 0;

  const sizeClasses = {
    sm: 'text-xs px-2 py-1',
    md: 'text-sm px-3 py-1.5',
    lg: 'text-base px-4 py-2'
  };

  const iconSizes = {
    sm: 'h-3 w-3',
    md: 'h-4 w-4', 
    lg: 'h-5 w-5'
  };

  if (variant === 'compact') {
    return (
      <div className="flex items-center gap-2">
        <Badge 
          className={`bg-gradient-to-r from-lottery-purple to-lottery-pink text-white font-semibold ${sizeClasses[size]} flex items-center gap-1`}
        >
          <Coins className={iconSizes[size]} />
          {kairosBalance} Kairos
        </Badge>
        {showConvertButton && raivanBalance >= 18 && (
          <Button
            size="sm"
            variant="outline"
            onClick={handleConvertTokens}
            className="text-xs"
          >
            Convertir
          </Button>
        )}
      </div>
    );
  }

  return (
    <div className="flex flex-col gap-2">
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <Coins className="h-5 w-5 text-lottery-purple" />
          <span className="font-semibold text-gray-900">Tokens Disponibles</span>
        </div>
        {showConvertButton && (
          <Button
            size="sm"
            variant="outline"
            onClick={handleConvertTokens}
            className="text-xs"
          >
            Gestionar
          </Button>
        )}
      </div>

      <div className="grid grid-cols-2 gap-3">
        <Badge 
          className="bg-gradient-to-r from-lottery-purple to-lottery-pink text-white font-semibold px-3 py-2 flex items-center justify-center gap-1"
        >
          <Coins className="h-4 w-4" />
          {kairosBalance} Kairos
        </Badge>

        <Badge 
          variant="outline"
          className="border-lottery-gold text-lottery-gold font-semibold px-3 py-2 flex items-center justify-center gap-1"
        >
          <Coins className="h-4 w-4" />
          {raivanBalance} Raivan
        </Badge>
      </div>

      {raivanBalance >= 18 && (
        <div className="text-xs text-gray-600 text-center">
          Puedes convertir {Math.floor(raivanBalance / 18)} Kairos m√°s
        </div>
      )}
    </div>
  );
}

client/src/components/navigation-dropdow.content:
import React from "react";
import { Link, useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { 
  DropdownMenu, 
  DropdownMenuContent, 
  DropdownMenuItem, 
  DropdownMenuSeparator,
  DropdownMenuTrigger,
  DropdownMenuLabel
} from "@/components/ui/dropdown-menu";
import { Badge } from "@/components/ui/badge";
import { useLanguage } from "@/lib/i18n";
import { 
  Menu, 
  Home, 
  Gamepad2, 
  ShoppingBag, 
  User, 
  Trophy, 
  Users,
  MapPin,
  Fingerprint,
  Globe,
  Coins,
  ChevronDown,
  Ticket
} from "lucide-react";

interface NavigationItem {
  href: string;
  labelKey: keyof import("@/lib/i18n").Translations;
  icon: React.ComponentType<{ className?: string }>;
  badge?: string;
  description?: string;
}

const navigationItems: NavigationItem[] = [
  { href: "/dashboard", labelKey: "dashboard", icon: Home, description: "Main Dashboard" },
  { href: "/lotteries", labelKey: "lotteries", icon: Gamepad2, description: "Travel Lotteries" },
  { href: "/ticket-history", labelKey: "ticketHistory", icon: Ticket, description: "My Lottery Tickets" },
  { href: "/marketplace", labelKey: "marketplace", icon: ShoppingBag, description: "Token Marketplace" },
  { href: "/missions", labelKey: "missions", icon: MapPin, description: "Travel Missions" },
  { href: "/token-management", labelKey: "tokenManagement", icon: Coins, badge: "Viator", description: "Token Management" },
  { href: "/profile", labelKey: "profile", icon: User, description: "User Profile" },
  { href: "/winner-dashboard", labelKey: "myPrizes", icon: Trophy, description: "My Prizes" },
  { href: "/affiliate-dashboard", labelKey: "affiliateProgram", icon: Users, badge: "New", description: "Affiliate Program" },
  { href: "/country-operations", labelKey: "countryOperations", icon: Globe, badge: "Expansion", description: "Country Operations" },
  { href: "/unique-ids", labelKey: "uniqueIds", icon: Fingerprint, description: "Unique IDs" },
];

interface NavigationDropdownProps {
  currentPath?: string;
}

export default function NavigationDropdown({ currentPath }: NavigationDropdownProps) {
  const [location] = useLocation();
  const { t } = useLanguage();
  const current = currentPath || location;

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button 
          variant="outline" 
          className="flex items-center gap-2 border-explore-blue text-explore-blue hover:bg-explore-blue hover:text-white"
          data-testid="navigation-dropdown-trigger"
        >
          <Menu className="h-4 w-4" />
          <span>Navigation</span>
          <ChevronDown className="h-3 w-3" />
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent className="w-80" align="start" forceMount>
        <DropdownMenuLabel className="text-explore-blue font-semibold">
          üåü VoyageLotto Navigation
        </DropdownMenuLabel>
        <DropdownMenuSeparator />

        {navigationItems.map((item) => {
          const Icon = item.icon;
          const isActive = current === item.href;

          return (
            <Link key={item.href} href={item.href}>
              <DropdownMenuItem 
                className={`flex items-center gap-3 p-3 cursor-pointer ${
                  isActive ? 'bg-explore-blue/10 text-explore-blue' : ''
                }`}
                data-testid={`nav-dropdown-${item.href.replace('/', '')}`}
              >
                <Icon className={`h-4 w-4 ${isActive ? 'text-explore-blue' : 'text-muted-foreground'}`} />
                <div className="flex flex-col flex-1 min-w-0">
                  <div className="flex items-center gap-2">
                    <span className="font-medium">{t(item.labelKey)}</span>
                    {item.badge && (
                      <Badge 
                        variant={isActive ? "default" : "secondary"} 
                        className="text-xs px-1.5 py-0.5"
                      >
                        {item.badge}
                      </Badge>
                    )}
                  </div>
                  {item.description && (
                    <span className="text-xs text-muted-foreground">
                      {item.description}
                    </span>
                  )}
                </div>
              </DropdownMenuItem>
            </Link>
          );
        })}
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

